"Phone Number":{"type":"Phone"},
"Phone Numbers":{"type":"Phone"},
};

const unitDict = {
"Unit":{
	"4":"years",
	"8":"months",
	"8192":"weeks",
	"16":"days",
	"32":"hours",
	"64":"minutes",
	"128":"seconds"},
